__NUXT_JSONP__("/Z-OLD-ROUTES/1/stories", (function(a,b,c,d,e,f,g,h,i){return {data:[{_rev:"PrY6yJV3wLa1jqCBxGuBUJ",_type:e,subhead:{_type:a,en:"Meet the people"},pageMetadata:{pageTitle:{_type:a,en:"Meet the People"},_type:"pageMetadata"},_id:e,subheadText:{_type:"localeText",en:"Click below to meet some of the people in Tuchyn and learn about their life before the war."},_createdAt:"2023-09-29T20:12:35Z",mainText:{_type:"localeRte",en:{rte:[{children:[{_type:f,marks:[],text:"In the 1930s, Tuchyn was a shtetl, a predominantly Jewish town, in Poland. About two-thirds of the people in Tuchyn were Jewish. They lived and worked alongside Ukrainians and Poles. Rich farmland surrounded the town and people came from the rural areas to the market there.",_key:"3db41fba2d190"}],_type:g,style:h,_key:"e53dc06fda6f",markDefs:[]},{markDefs:[],children:[{_type:f,marks:[],text:"In 1933, thousands of miles away in Germany, Adolf Hitler and the Nazi Party came to power. They persecuted Jews and made them second-class citizens. In 1939, Germany launched World War II. When it invaded other countries, it imposed its antisemitic, or anti-Jewish, policies on the countries it occupied. The Germans did not come to Tuchyn initially. Instead, in 1939, the Soviets occupied this area of Poland.",_key:"846f08ffecf90"}],_type:g,style:h,_key:"2ba62522d424"}],_type:"rte"}},subjectCTA:{_type:a,en:"Read more"},title:{_type:a,en:"Tuchyn Before World War II"},_updatedAt:"2023-10-12T18:43:55Z"}],fetch:{"0":{content:{sections:{resistance:{_type:a,en:"Resistance"},aftermath:{_type:a,en:"Aftermath"},conclusion:{_type:a,en:"Conclusion"},occupation:{_type:a,en:"Occupation"},intro:{_type:a,en:"Introduction"}}},sectionsArr:["intro","occupation","resistance","aftermath","conclusion"],isExpanded:i},"1":{isExpanded:i,languages:{en:{name:"English"},uk:{name:"украї́нська"}}},"2":{subjects:{subject1:{img:{img:{_type:b,asset:{_ref:"image-73c51d4a94b01a07a0568e2435a00127caf3a8be-304x304-png",_type:c}},_type:d,alt:{_type:a,en:"Young girl in a dark dress."}},name:{en:"Luba Chomut",_type:a}},subject2:{img:{img:{asset:{_ref:"image-4fea0ae468b714083c898aff9e3558a7df051d61-304x304-png",_type:c},_type:b},_type:d,alt:{_type:a,en:"Adolescent boy in a shirt and jacket"}},name:{_type:a,en:"Yosef Zilberberg"}},subject3:{img:{img:{asset:{_ref:"image-d38c5b0475ab1692793ebd465d7db5d4da175ae1-304x304-png",_type:c},_type:b},_type:d,alt:{_type:a,en:"Young woman in 1940s era clothing"}},name:{en:"Mania Schwartzman",_type:a}}}}},mutations:[]}}("localeString","image","reference","img","intro1","span","block","normal",false)));