__NUXT_JSONP__("/Z-OLD-ROUTES/5", (function(a,b,c,d,e,f,g,h){return {data:[{conclusion:{btnText:{_type:a,en:"Start Over"},subhead:{_type:a,en:"As you leave the digital exhibit, consider these questions. How are people are treated as less-than in your community today? How can we as a community prevent intolerance and violence in all of its forms?"},title:{_type:a,en:"With this exhibit, we remember what happened."}},featuredQuote:{bgImg:{caption:{en:"Painting of Doves, by Yosef Zilberberg",_type:a},credit:{_type:a,en:"Courtesy Yosef Zilberberg."},img:{_type:b,asset:{_ref:"image-262b4280ef2ca6eeb60e39701dd7c3e41437111c-2125x1657-jpg",_type:c}},_type:d},quote:{attr:{_type:a,en:"Dinos Christianopoulos"},quote:{_type:e,en:"They tried to bury us, they didn’t know we were seeds."},_type:"contentQuote"}},_rev:"e3V39y67Gh5rKgMn8CEdMW",_type:f,_updatedAt:"2023-10-24T22:32:22Z",_createdAt:"2023-09-29T20:23:46Z",pageMetadata:{pageTitle:{en:g,_type:a},_type:"pageMetadata"},_id:f,section1:{text:{en:"In 2011, Ze’ev Portnoy, Yosef Zilberberg, and Miriam Schwartzman Koutz’s son Gideon erected a monument on the site of the Tuchyn ghetto in honor of the uprising and the lives lost.",_type:e},img2:{img:{_type:b,asset:{_ref:"image-a85efc4abfbc2b26c756784b2bec2c498b6e6533-2000x965-jpg",_type:c}},_type:d,alt:{_type:a,en:"Close up Image of the text of the monument. Text reads In memory of three thousand Jews, residents of Tuchin, who raised the banner of revolt in the Tuchin ghetto. Murdered, burned, and buried alive by murderers, in September 1942. In honor of their sacred memory!"}},img1:{alt:{_type:a,en:"Photograph of a four-tiered stone monument with Ukrainian text on the lower tiers and a Star of David on the top tier."},caption:{_type:a,en:"Photograph of Memorial Monument in Tuchyn, 2016"},credit:{_type:a,en:"Jared McBride"},img:{_type:b,asset:{_ref:"image-d00654600a623cb2577aef422d720a9aba6bd3ee-1774x1813-jpg",_type:c}},_type:d}},section2:{text:{_type:e,en:"The places where people died lie unmarked today. No traces remain of the massacres that happened there."},img1:{_type:d,alt:{_type:a,en:"Photograph of a green field with ruins of a single building in the background."},caption:{_type:a,en:"Jewish Cemetery in Tuchyn, 2020"},credit:{_type:a,en:"Courtesy Nataliia Ivchyk."},img:{_type:b,asset:{_ref:"image-42f1a7610b18257b5939b86e68c2ade075a14a56-774x436-jpg",_type:c}}}}}],fetch:{"0":{content:{sections:{resistance:{_type:a,en:"Resistance"},aftermath:{_type:a,en:"Aftermath"},conclusion:{_type:a,en:g},occupation:{_type:a,en:"Occupation"},intro:{_type:a,en:"Introduction"}}},sectionsArr:["intro","occupation","resistance","aftermath","conclusion"],isExpanded:h},"1":{isExpanded:h,languages:{en:{name:"English"},uk:{name:"украї́нська"}}}},mutations:[]}}("localeString","image","reference","img","localeText","intro5","Conclusion",false)));