__NUXT_JSONP__("/aftermath/stories/1", (function(a,b,c,d,e,f,g,h,i,j,k,l,m,n,o,p,q,r,s,t,u,v,w,x,y,z){return {data:[{nav:{nav:{next:{_type:a,en:"Move on to the Conclusion"},prev:{_type:a,en:"Back to All Stories"},_type:"navLabel"}},metadata:{pageMetadata:{pageTitle:{_type:a,en:k},_type:"pageMetadata"}}}],fetch:{"0":{content:{sections:{resistance:{_type:a,en:l},aftermath:{_type:a,en:m},conclusion:{_type:a,en:n},occupation:{_type:a,en:o},intro:{_type:a,en:p}}},sectionsArr:["intro","occupation","resistance","aftermath","conclusion"],isExpanded:b},"1":{isExpanded:b,languages:{en:{name:"English"},uk:{name:"украї́нська"}}},"2":{content:{settings:{sections:{resistance:{_type:a,en:l},aftermath:{_type:a,en:m},conclusion:{_type:a,en:n},occupation:{_type:a,en:o},intro:{_type:a,en:p}}},subjects:{subject1:{img:{img:{_type:f,asset:{_ref:"image-73c51d4a94b01a07a0568e2435a00127caf3a8be-304x304-png",_type:g}},_type:h,alt:{_type:a,en:"Young girl in a dark dress."}},name:{en:k,_type:a}},subject2:{img:{img:{_type:f,asset:{_ref:"image-4fea0ae468b714083c898aff9e3558a7df051d61-304x304-png",_type:g}},_type:h,alt:{_type:a,en:"Adolescent boy in a shirt and jacket"}},name:{_type:a,en:"Yosef Zilberberg"}},subject3:{img:{img:{_type:f,asset:{_ref:"image-d38c5b0475ab1692793ebd465d7db5d4da175ae1-304x304-png",_type:g}},_type:h,alt:{en:"Young woman in 1940s era clothing",_type:a}},name:{_type:a,en:"Mania Schwartzman"}}}}},"3":{content:{sections:[{align:"right",vid:{_type:q,id:"650434630",vidCaption:{en:"Listen to hear her speak about her sister’s death.",_type:a}},_type:r,text:{en:{_type:i,rte:[{style:c,_key:"911990c4705d",markDefs:[],children:[{_type:d,marks:[],text:"Luba Chomut and her parents survived the mass executions in Tuchyn. They lived because a Ukrainian farmer Pavlo Gerasimchik hid them in his barn for eighteen months. Pavlo made a hole in one of his haystacks, bringing them bread and water.",_key:"db0ffd724da60"}],_type:e},{markDefs:[],children:[{_key:"86a9f5b7527a0",_type:d,marks:[],text:"Luba’s older sister, Chana, was murdered by Ukrainians at age eleven. Itzak and Perla had sent Chana to live with the Ukrainian principal so she might pass as Christian and survive. The principal’s wife forced Chana out when German officials gave the order to kill Christian families that hid Jewish children."}],_type:e,style:c,_key:"fdd389b85eb4"},{markDefs:[],children:[{_type:d,marks:[],text:"Luba and her parents did not find out about Chana’s death until after liberation.",_key:"c58bc989889d0"}],_type:e,style:c,_key:"2d17a7ca3714"}]},_type:j},_key:"d60176c154b7"},{img:{caption:{_type:a,en:"Chomut Family, Courtesy Michael Emmett."},img:{_type:f,asset:{_ref:"image-5fe509cfbb75d6ec56a70f10424e99071dbf1c8e-840x601-jpg",_type:g}},_type:h},_type:"contentImgText",text:{en:{rte:[{_key:"e34b07a82b7a",markDefs:[],children:[{_type:d,marks:[],text:"Much of Luba’s family in Tuchyn was killed during the Holocaust. In this portrait, the Stars of David denote her family members who perished.",_key:"43c2374ab9130"}],_type:e,style:c}],_type:i},_type:j},_key:"50be15bcc25b",imgAlign:s},{text:{_type:j,en:{rte:[{markDefs:[],children:[{_type:d,marks:[],text:"After the Russians liberated Tuchyn, Luba’s family spent three years in a displaced persons camp in Linz, Austria. There, the Chomut family changed their name to Emmett and Luba took the name Laura. Her parents went by Pearl and Isaak and they had a son Michael in the camp. ",_key:"776e3e0a7de70"}],_type:e,style:c,_key:"feb14c62ca10"},{markDefs:[],children:[{_type:d,marks:[],text:"The family originally hoped to settle in Israel (then Palestine) but they ultimately immigrated to the United States in 1949. ",_key:"35867b05d64f"}],_type:e,style:c,_key:"2fb798122553"}],_type:i}},_key:"930da33a95e4",align:s,vid:{_type:q,id:"650434949",vidCaption:{_type:a,en:"Hear Laura speak about her family’s migration to the United States."}},_type:r},{_type:"contentText",text:{en:{_type:i,rte:[{_type:e,style:c,_key:"4ef16142886d",markDefs:[],children:[{_type:d,marks:[],text:"Pavlo Gerasimchik, his wife Lubka, and their children were honored as The Righteous Among the Nations for their actions. This award honors people who risked their own lives to save Jewish people during the Holocaust. ",_key:"00b0e49325040"}]},{markDefs:[],children:[{marks:[],text:"Authors published two books about Laura’s experiences: Brisko and The Girl in the Haystack. These works teach about her family’s survival in the face of hatred and antisemitism. ",_key:"31c4d52f44df",_type:d}],_type:e,style:c,_key:"4febbd64acea"},{children:[{_type:d,marks:[],text:"Today, Laura Emmett Oberlender lives in Florida and New Jersey. She is a mother of three and grandmother of six.",_key:"c6a27d49c067"}],_type:e,style:c,_key:"49fe6099ee8b",markDefs:[]}]},_type:j},_key:"13a4dc7ee70d"}]}},"4":{labels:{pause:{_type:a,en:t},play:{_type:a,en:u}},options:{controls:v,loop:b,autoplay:b,muted:b,portrait:b,title:b,byline:b},isPlaying:b},"5":{labels:{pause:{_type:a,en:t},play:{_type:a,en:u}},options:{controls:v,loop:b,autoplay:b,muted:b,portrait:b,title:b,byline:b},isPlaying:b},"6":{labels:{prev:{uk:w,_type:a,en:x},next:{uk:y,_type:a,en:z}},isCollapsed:b},"7":{labels:{prev:{uk:w,_type:a,en:x},next:{uk:y,_type:a,en:z}},isCollapsed:b}},mutations:[]}}("localeString",false,"normal","span","block","image","reference","img","rte","localeRte","Luba Chomut","Resistance","Aftermath","Conclusion","Occupation","Introduction","vid","contentVideo","left","Pause","Play",true,"наступний розділ","Previous Section","попередній розділ","Next Section")));