__NUXT_JSONP__("/aftermath/stories/2", (function(a,b,c,d,e,f,g,h,i,j,k,l,m,n,o,p,q,r,s,t,u,v,w,x,y,z){return {data:[{nav:{nav:{next:{_type:a,en:"Move on to the Conclusion"},prev:{_type:a,en:"Back to All Stories"},_type:"navLabel"}},metadata:{pageMetadata:{pageTitle:{en:k,_type:a},_type:"pageMetadata"}}}],fetch:{"0":{content:{sections:{occupation:{_type:a,en:l},intro:{_type:a,en:m},resistance:{_type:a,en:n},aftermath:{_type:a,en:o},conclusion:{_type:a,en:p}}},sectionsArr:["intro","occupation","resistance","aftermath","conclusion"],isExpanded:b},"1":{isExpanded:b,languages:{en:{name:"English"},uk:{name:"украї́нська"}}},"2":{content:{settings:{sections:{occupation:{_type:a,en:l},intro:{_type:a,en:m},resistance:{_type:a,en:n},aftermath:{_type:a,en:o},conclusion:{_type:a,en:p}}},subjects:{subject1:{img:{img:{_type:f,asset:{_ref:"image-73c51d4a94b01a07a0568e2435a00127caf3a8be-304x304-png",_type:g}},_type:h,alt:{en:"Young girl in a dark dress.",_type:a}},name:{_type:a,en:"Luba Chomut"}},subject2:{img:{_type:h,alt:{_type:a,en:"Adolescent boy in a shirt and jacket"},img:{_type:f,asset:{_type:g,_ref:"image-4fea0ae468b714083c898aff9e3558a7df051d61-304x304-png"}}},name:{_type:a,en:k}},subject3:{img:{img:{_type:f,asset:{_ref:"image-d38c5b0475ab1692793ebd465d7db5d4da175ae1-304x304-png",_type:g}},_type:h,alt:{_type:a,en:"Young woman in 1940s era clothing"}},name:{_type:a,en:"Mania Schwartzman"}}}}},"3":{content:{sections:[{text:{_type:i,en:{rte:[{_key:"7daab03f528b",markDefs:[],children:[{_key:"67cea2b3a55d0",_type:c,marks:[],text:"During the war, Yosef Zilberberg and his family survived in hiding. Anton Ozertchuk, a Ukrainian, hid them on his property. They lived for over a year in miserable conditions, clustered together under a barn that held sheep. While Yosef’s immediate family survived the war, the majority of his family perished. Many of Yosef’s aunts, uncles, and cousins died at the hands of the Germans and Ukrainians."}],_type:d,style:e},{children:[{_type:c,marks:[],text:"The Zilberberg family continued to hide with Anton for a time after the liberation of the area by Russian forces.",_key:"5cbba70cb3ca0"}],_type:d,style:e,_key:"5acb541463a0",markDefs:[]}],_type:j}},_key:"0333169af3df",align:q,vid:{_type:r,id:"650436733",vidCaption:{_type:a,en:"Hear Yosef recall a poignant memory with Anton."}},_type:s},{_key:"d564d009a9a7",img:{caption:{_type:a,en:"Anton Ozertrchuk brings water to a hiding place under his barn, by Yosef Zilberberg"},credit:{_type:a,en:"Courtesy of Yosef Zilberberg."},img:{asset:{_ref:"image-59a1d546f50e6204a6db851edccf6ff9fbe9d7b1-2084x1499-jpg",_type:g},_type:f},_type:h},_type:"contentImgFull"},{_key:"ce01d167bd55",align:q,vid:{_type:r,id:"650436931",vidCaption:{_type:a,en:"Listen to hear Yosef talk about his family’s decision to move."}},_type:s,text:{_type:i,en:{rte:[{markDefs:[],children:[{_type:c,marks:[],text:"The family feared the unstable Soviet regime, but eventually left hiding to move to Rovno. Following the end of the war in 1945, Yosef and the Zilberberg family moved to Lodz and later they went to Austria. ",_key:"bf57e73bb4e10"}],_type:d,style:e,_key:"b055716cd8a0"},{style:e,_key:"a47b4f62eb40",markDefs:[],children:[{_type:c,marks:[],text:"They eventually took part in the “Bricha,” which meant Escape, in which Jews illegally made aliyah, or emigrated, to Israel (then Palestine). They spent two years living in a tent camp there because of the poor economic conditions.",_key:"4d8c32269951"}],_type:d}],_type:j}}},{_key:"7f5e410bd183",imgAlign:"left",img:{img:{_type:f,asset:{_ref:"image-eef9e4474e35df8022b9f78212263204d98c67a0-2000x1476-jpg",_type:g}},_type:h,caption:{en:"A painting by Yosef Zilberberg depicting the pogrom in Tuchyn",_type:a},credit:{_type:a,en:"Courtesy Yosef Zilberberg"}},_type:"contentImgText",text:{_type:i,en:{_type:j,rte:[{style:e,_key:"2b799ac7d66f",markDefs:[],children:[{_key:"e919ede62dba0",_type:c,marks:[],text:"Currently Yosef resides in Israel where he pursued painting. While there are no photographs of the Tuchyn massacres, he has illustrated what happened. "}],_type:d},{_type:d,style:e,_key:"e6e23bf8923e",markDefs:[],children:[{_type:c,marks:[],text:"Anton Ozertchuk and his family were honored as Righteous Among the Nations for saving the lives of the Zilberberg family. Yosef himself later thanked the Ozertchuks by sending financial support and visiting them.",_key:"2734d49e4a89"}]}]}}}]}},"4":{labels:{pause:{_type:a,en:t},play:{_type:a,en:u}},options:{controls:v,loop:b,autoplay:b,muted:b,portrait:b,title:b,byline:b},isPlaying:b},"5":{labels:{pause:{_type:a,en:t},play:{_type:a,en:u}},options:{controls:v,loop:b,autoplay:b,muted:b,portrait:b,title:b,byline:b},isPlaying:b},"6":{labels:{prev:{uk:w,_type:a,en:x},next:{uk:y,_type:a,en:z}},isCollapsed:b},"7":{labels:{prev:{uk:w,_type:a,en:x},next:{uk:y,_type:a,en:z}},isCollapsed:b}},mutations:[]}}("localeString",false,"span","block","normal","image","reference","img","localeRte","rte","Yosef Zilberberg","Occupation","Introduction","Resistance","Aftermath","Conclusion","right","vid","contentVideo","Pause","Play",true,"наступний розділ","Previous Section","попередній розділ","Next Section")));