__NUXT_JSONP__("/aftermath/stories/3", (function(a,b,c,d,e,f,g,h,i,j,k,l,m,n,o,p,q,r,s,t,u,v,w,x,y){return {data:[{nav:{nav:{next:{en:"Move on to the Conclusion",_type:a},prev:{_type:a,en:"Back to All Stories"},_type:"navLabel"}},metadata:{pageMetadata:{pageTitle:{_type:a,en:i},_type:"pageMetadata"}}}],fetch:{"0":{content:{sections:{conclusion:{_type:a,en:l},occupation:{_type:a,en:m},intro:{_type:a,en:n},resistance:{_type:a,en:o},aftermath:{_type:a,en:p}}},sectionsArr:["intro","occupation","resistance","aftermath","conclusion"],isExpanded:b},"1":{isExpanded:b,languages:{en:{name:"English"},uk:{name:"украї́нська"}}},"2":{content:{settings:{sections:{resistance:{_type:a,en:o},aftermath:{_type:a,en:p},conclusion:{_type:a,en:l},occupation:{_type:a,en:m},intro:{_type:a,en:n}}},subjects:{subject1:{img:{img:{_type:f,asset:{_ref:"image-73c51d4a94b01a07a0568e2435a00127caf3a8be-304x304-png",_type:g}},_type:h,alt:{_type:a,en:"Young girl in a dark dress."}},name:{_type:a,en:"Luba Chomut"}},subject2:{img:{img:{_type:f,asset:{_ref:"image-4fea0ae468b714083c898aff9e3558a7df051d61-304x304-png",_type:g}},_type:h,alt:{en:"Adolescent boy in a shirt and jacket",_type:a}},name:{_type:a,en:"Yosef Zilberberg"}},subject3:{img:{img:{_type:f,asset:{_ref:"image-d38c5b0475ab1692793ebd465d7db5d4da175ae1-304x304-png",_type:g}},_type:h,alt:{_type:a,en:"Young woman in 1940s era clothing"}},name:{_type:a,en:i}}}}},"3":{content:{sections:[{_key:"82ba4bef3c93",attr:{_type:a,en:i},quote:{_type:"localeText",en:"That night I left Tuchyn with my sister Basia. My mother Rivka and my brothers David and Liova were killed in the ghetto. Out of all of our family, including uncles and aunts and their families, only we remained, my sister Basia and I."},_type:"contentQuote"},{_key:"fced166219cd",align:"right",vid:{_type:q,id:"650435924",vidCaption:{_type:a,en:"Listen to her son tell the story of life after the war."}},_type:r,text:{en:{rte:[{_type:c,style:d,_key:"02d2b124dcbd",markDefs:[],children:[{_type:e,marks:[],text:"During the war, Mania lost most of her family, as her brother Leib died serving in the Russian army. Mania fled from the ghetto together with her little sister, Basia. Their Polish nanny Zosia hid them at first. Then, she moved the sisters to their neighbors, who hid them in the basement.",_key:"f50c9eae63700"}]},{style:d,_key:"9d15e79e8562",markDefs:[],children:[{_type:e,marks:[],text:"Later, Mania and Basia left Tuchyn with falsified papers. Mania took the name of a Polish friend Maria and passed the war with her sister hidden by and working for a Czech family.",_key:"de713d2ac168"}],_type:c},{_key:"6998bf71c497",markDefs:[],children:[{_key:"72833be8caa30",_type:e,marks:[],text:"After the war, Maria (formerly Mania) met her future husband Alexander Kouts. Alexander was from a neighboring village of Berezne and led a group of Russian partisans who fought against the German forces. "}],_type:c,style:d},{_type:c,style:d,_key:"76870683521b",markDefs:[],children:[{text:"They decided not to stay in the Soviet Union but go to Poland. First, they went to Lodz, where Alexander had high government responsibilities. They got married in 1945 and in 1948 their son Eugeniusz was born.",_key:"2a29a5c07f55",_type:e,marks:[]}]}],_type:j},_type:k}},{text:{_type:k,en:{rte:[{style:d,_key:"7250478acb67",markDefs:[],children:[{marks:[],text:"In 1950, Maria and Alex moved to the Polish capital Warsaw, which was ruined during the war. After the war people began rebuilding the city and it felt like a “revival” for Maria. She loved city life.",_key:"37c7efa1ff240",_type:e}],_type:c}],_type:j}},_key:"dc549d8be05d",imgAlign:"left",img:{img:{_type:f,asset:{_type:g,_ref:"image-8b00c2a734df422dd5574e16fcb5630129b7e5d0-871x591-jpg"}},_type:h,caption:{en:" Photo of Maria (Mania) Schwartzman Kouts, Alexander Kouts and Basia Schwartzman, c. 1940s",_type:a},credit:{en:"Courtesy Gideon Kouts",_type:a}},_type:"contentImgText"},{_key:"69a99880fa58",vid:{vidCaption:{_type:a,en:"Hear her son Gideon speak about his mother and this difficult past."},_type:q,id:"650435517"},_type:r,text:{_type:k,en:{rte:[{markDefs:[],children:[{_type:e,marks:[],text:"Maria, her husband Alexander, Basia, and their son Eugeniusz eventually made aliyah, or emigrated, to Tel Aviv in Israel in 1957. There, Maria took the name Miriam, Basia became Batia, and Eugeniusz took the name Gideon. ",_key:"0c436277abdd0"}],_type:c,style:d,_key:"41dc66b6aea1"},{_type:c,style:d,_key:"80a9b99e8d11",markDefs:[],children:[{_type:e,marks:[],text:"Basia had two sons, Ronny and David. Ronny was killed serving his country as an Israeli soldier during the Lebanon War. Miriam passed away in 2008. ",_key:"1d8b0e217dd5"}]}],_type:j}}}]}},"4":{labels:{pause:{_type:a,en:s},play:{_type:a,en:t}},options:{controls:u,loop:b,autoplay:b,muted:b,portrait:b,title:b,byline:b},isPlaying:b},"5":{labels:{pause:{_type:a,en:s},play:{_type:a,en:t}},options:{controls:u,loop:b,autoplay:b,muted:b,portrait:b,title:b,byline:b},isPlaying:b},"6":{labels:{prev:{uk:v,_type:a,en:w},next:{uk:x,_type:a,en:y}},isCollapsed:b},"7":{labels:{prev:{uk:v,_type:a,en:w},next:{uk:x,_type:a,en:y}},isCollapsed:b}},mutations:[]}}("localeString",false,"block","normal","span","image","reference","img","Mania Schwartzman","rte","localeRte","Conclusion","Occupation","Introduction","Resistance","Aftermath","vid","contentVideo","Pause","Play",true,"наступний розділ","Previous Section","попередній розділ","Next Section")));