__NUXT_JSONP__("/intro", (function(a,b,c,d,e){return {data:[{nav:{next:{_type:a,en:"Meet the people"},_type:"nextNavLabel"},_rev:"jYvXCiZy3X0BYYAObTDlBq",bgImg:{img:{_type:"image",asset:{_ref:"image-0769925974c0c9ea62776b98f8f15141fdff592b-1959x1331-jpg",_type:"reference"}},_type:"img",caption:{_type:a,en:"Escape into the Forests, Painting by Yosef Zilberberg"},credit:{_type:a,en:"Courtesy Yosef Zilberberg"}},_id:d,_updatedAt:"2023-10-25T23:17:26Z",panels:[{_type:b,en:"Far away in Ukraine sits a small town named Tuchyn. Farmland and forests surround it.",_key:"f59e618545e4"},{_type:b,en:"The German army took over Tuchyn in July 1941. The Germans and their Ukrainian helpers constantly persecuted the Jewish community and treated them with violence.",_key:"ca590044b8d1"},{en:"In September 1942, the Jewish residents of Tuchyn learned that the Germans planned to kill them all. The Jews resisted by setting fire to their homes and fighting back.",_key:"f15b6cbd619a",_type:b},{_type:b,en:"Thousands of people fled to the forests but many were caught. Almost all Tuchyn’s Jews perished at the hands of the Germans and their own Ukrainian neighbors.",_key:"6b4cf33a941c"},{_key:"4f52f4178f81",_type:b,en:"In this exhibition, you will learn about this “Holocaust by Bullets” through family stories. You will find out about many forms of resistance during this mass genocide."},{_type:b,en:"Unearth these roots of resistance. Share these stories with people you know. Consider how you can stand up to intolerance in your own lives.",_key:"f551644b71a7"}],_createdAt:"2023-10-02T19:26:32Z",_type:d,pageMetadata:{pageTitle:{_type:a,en:e},_type:"pageMetadata"}}],fetch:{"0":{content:{sections:{resistance:{_type:a,en:"Resistance"},aftermath:{_type:a,en:"Aftermath"},conclusion:{_type:a,en:"Conclusion"},occupation:{_type:a,en:"Occupation"},intro:{_type:a,en:e}}},sectionsArr:["intro","occupation","resistance","aftermath","conclusion"],isExpanded:c},"1":{isExpanded:c,languages:{en:{name:"English"},uk:{name:"украї́нська"}}},"2":{labels:{prev:{uk:"наступний розділ",_type:a,en:"Previous Section"},next:{uk:"попередній розділ",_type:a,en:"Next Section"}},isCollapsed:c}},mutations:[]}}("localeString","localeText",false,"landing1","Introduction")));