__NUXT_JSONP__("/intro/stories/1", (function(a,b,c,d,e,f,g,h,i,j,k,l,m,n,o,p,q,r,s,t,u,v){return {data:[{nav:{nav:{next:{en:"Explore Map of Tuchyn",_type:a},prev:{_type:a,en:"Back to All Stories"},_type:"navLabel"}},metadata:{pageMetadata:{_type:"pageMetadata",pageTitle:{_type:a,en:"Meet Luba Chomut"}}}}],fetch:{"0":{content:{sections:{conclusion:{_type:a,en:l},occupation:{_type:a,en:m},intro:{_type:a,en:n},resistance:{_type:a,en:o},aftermath:{_type:a,en:p}}},sectionsArr:["intro","occupation","resistance","aftermath","conclusion"],isExpanded:h},"1":{isExpanded:h,languages:{en:{name:"English"},uk:{name:"украї́нська"}}},"2":{content:{sections:[{img:{_type:b,caption:{_type:a,en:"Chana, Dvora, and Luba Chomut, c. 1937"},credit:{en:q,_type:a},img:{_type:c,asset:{_type:d,_ref:"image-c7ecc4adc32a8a78c54f7e66eb61f8124f7631f9-831x1202-jpg"}}},_type:i,text:{_type:j,en:{rte:[{markDefs:[],children:[{_type:e,marks:[],text:"Luba Chomut was born in Tuchyn in 1934. Her parents Itzak and Perla named her Luba, which means “love.” She had a sister who was three years older than her named Chana.",_key:"1a76edcaed800"}],_type:f,style:g,_key:"f2cbeec5159d"}],_type:k}},_key:"371f39830f27",imgAlign:r},{_key:"0591f4143a19",imgAlign:r,img:{img:{_type:c,asset:{_ref:"image-3fe9bd4eb89d5019339714ed7e4a5712b56de99d-2048x2541-jpg",_type:d}},_type:b,caption:{en:"Walking Home from Synagogue [in Tuchyn], by Yosef Zilberberg",_type:a},credit:{_type:a,en:"Courtesy Yosef Zilberberg"}},_type:i,text:{en:{rte:[{markDefs:[],children:[{_type:e,marks:[],text:"Luba’s favorite day of the week was Shabbat, a holiday and day of rest that happened every Saturday. On Shabbat, she wore her blue coat and leather boots that she laced up to her knees. While many adults went to synagogue, the Jewish place of worship, Luba loved walking around town with Chana looking at the shops.",_key:"19abd9e0190e0"}],_type:f,style:g,_key:"ab264752cb0c"}],_type:k},_type:j}},{imgAlign:"right",img:{img:{_type:c,asset:{_ref:"image-80bfbe6961a33f9fd713d6665bb7e5adfc7b380e-1598x914-jpg",_type:d}},_type:b,caption:{_type:a,en:"Chomut Family, c. 1930s"},credit:{_type:a,en:q}},_type:i,text:{_type:j,en:{rte:[{children:[{marks:[],text:"Luba had a large family, with many aunts and uncles and cousins. Her father and Uncle Fridal were well-respected members of their community.",_key:"41634895f40b0",_type:e}],_type:f,style:g,_key:"87b561be4570",markDefs:[]},{markDefs:[],children:[{_type:e,marks:[],text:"While Luba had some good memories of Tuchyn, it was a hard place for the Jewish residents to live. Some Ukrainians called Jews names and threw stones at them.",_key:"bc8b4bc482630"}],_type:f,style:g,_key:"0b9b9ca5ee37"},{_type:f,style:g,_key:"a1efb70bd0cf",markDefs:[],children:[{_type:e,marks:[],text:"In 1939, when Luba was five, war broke out and Russians took over the town. In those years, her family moved into a new house. But, they also lived in fear that the Soviets might send them away to Siberia.",_key:"cb4d71d8f52d0"}]},{style:g,_key:"1c1ef0978c70",markDefs:[],children:[{marks:[],text:"In the summer of 1941, the Germans invaded the Soviet Union and took over Tuchyn. In this exhibition, you will learn what happened under the Germans by hearing more about the Chomuts and other Jewish families in Tuchyn.",_key:"a234c0f589540",_type:e}],_type:f}],_type:k}},_key:"de457cae8131"}]}},"3":{content:{settings:{sections:{resistance:{_type:a,en:o},aftermath:{_type:a,en:p},conclusion:{_type:a,en:l},occupation:{_type:a,en:m},intro:{_type:a,en:n}}},subjects:{subject1:{img:{img:{_type:c,asset:{_ref:"image-73c51d4a94b01a07a0568e2435a00127caf3a8be-304x304-png",_type:d}},_type:b,alt:{_type:a,en:"Young girl in a dark dress."}},name:{_type:a,en:"Luba Chomut"}},subject2:{img:{img:{_type:c,asset:{_ref:"image-4fea0ae468b714083c898aff9e3558a7df051d61-304x304-png",_type:d}},_type:b,alt:{en:"Adolescent boy in a shirt and jacket",_type:a}},name:{_type:a,en:"Yosef Zilberberg"}},subject3:{img:{img:{_type:c,asset:{_ref:"image-d38c5b0475ab1692793ebd465d7db5d4da175ae1-304x304-png",_type:d}},_type:b,alt:{_type:a,en:"Young woman in 1940s era clothing"}},name:{_type:a,en:"Mania Schwartzman"}}}}},"4":{labels:{prev:{uk:s,_type:a,en:t},next:{uk:u,_type:a,en:v}},isCollapsed:h},"5":{labels:{prev:{uk:s,_type:a,en:t},next:{uk:u,_type:a,en:v}},isCollapsed:h}},mutations:[]}}("localeString","img","image","reference","span","block","normal",false,"contentImgText","localeRte","rte","Conclusion","Occupation","Introduction","Resistance","Aftermath","Courtesy Michael Emmett","left","наступний розділ","Previous Section","попередній розділ","Next Section")));