__NUXT_JSONP__("/intro/stories/2", (function(a,b,c,d,e,f,g,h,i,j,k,l,m,n,o,p,q,r,s,t,u,v){return {data:[{nav:{nav:{next:{_type:a,en:"Explore Map of Tuchyn"},prev:{_type:a,en:"Back to All Stories"},_type:"navLabel"}},metadata:{pageMetadata:{pageTitle:{_type:a,en:"Meet Yosef Zilberberg"},_type:"pageMetadata"}}}],fetch:{"0":{content:{sections:{occupation:{_type:a,en:l},intro:{_type:a,en:m},resistance:{_type:a,en:n},aftermath:{en:o,_type:a},conclusion:{_type:a,en:p}}},sectionsArr:["intro","occupation","resistance","aftermath","conclusion"],isExpanded:h},"1":{isExpanded:h,languages:{en:{name:"English"},uk:{name:"украї́нська"}}},"2":{content:{sections:[{_key:"814888f71193",imgAlign:"right",img:{img:{_type:b,asset:{_ref:"image-8fb4a6b1bcf659863635acfa159ac24ab49744d8-2067x2883-jpg",_type:c}},_type:d,caption:{_type:a,en:"Asia Zilberberg, by Yosef Zilberberg "},credit:{_type:a,en:q}},_type:i,text:{_type:j,en:{rte:[{_key:"dfaf7f9086ae",markDefs:[],children:[{_type:e,marks:[],text:"Yosef Zilberberg was born in Tuchyn in 1933. He lived with his parents Yaakov and Asia and his two older brothers, Itkhak and Aharon. Yosef loved to paint, and he produced many paintings of his family and Tuchyn when he was older.",_key:"3e03791558900"}],_type:f,style:g}],_type:k}}},{img:{caption:{_type:a,en:"Yaakov Zilberberg, by Yosef Zilberberg, Courtesy Yosef Zilberberg."},img:{_type:b,asset:{_ref:"image-69d427dffbcf543d389ed7e73385cf458c1925e8-2059x1520-jpg",_type:c}},_type:d},_type:"contentImgFull",_key:"e3bfa04d6cad"},{img:{img:{_type:b,asset:{_ref:"image-4669b9f32458192f082dc9605dcbc0c17605466c-2024x1473-jpg",_type:c}},_type:d,caption:{_type:a,en:"Best of Our House, by Yosef Zilberberg"},credit:{_type:a,en:q}},_type:i,text:{en:{rte:[{markDefs:[],children:[{_type:e,marks:[],text:"Yosef and his family lived on a main road that had gardens and fruit trees all around them in private gardens. His mother grew gorgeous fresh vegetables for them to eat. She also sold them at their family’s general store.",_key:"b05cc8ec01160"}],_type:f,style:g,_key:"d6f27328e4f0"},{_type:f,style:g,_key:"2bdc38cc93c1",markDefs:[],children:[{_type:e,marks:[],text:"Yosef had many relatives in Tuchyn. Every night people from his extended family came over to their house to drink tea and tell stories. Yosef also lived near his cousin Avraham Elbert, who was a few years older.",_key:"f2d2b3ade4760"}]}],_type:k},_type:j},_key:"af0ac2909f1c",imgAlign:r},{_type:i,text:{_type:j,en:{rte:[{markDefs:[],children:[{_type:e,marks:[],text:"Yosef had good memories of Tuchyn, and his family lived alongside their Ukrainian neighbors and did business with them. But, this area was often a hard place for Jews to live.",_key:"eaf7767b237d0"}],_type:f,style:g,_key:"3ec25d04a975"},{markDefs:[],children:[{_type:e,marks:[],text:"In 1939, when Yosef was six, war broke out. The Russians took over Tuchyn and it became hard for his family to sell goods from the store. His mother grew vegetables and his father sold milk and cheese from two cows.",_key:"d9290d068f9e0"}],_type:f,style:g,_key:"996f065ebe9c"},{_type:f,style:g,_key:"56717c26b6ac",markDefs:[],children:[{_type:e,marks:[],text:"In the summer of 1941, the Germans invaded the Soviet Union and drove the Russians out of the town. In this exhibition, you will learn what happened under the Germans by hearing more about the Zilberbergs and other Jewish families in Tuchyn.",_key:"6736c53f2da40"}]}],_type:k}},_key:"7ffbaad48452",imgAlign:r,img:{img:{asset:{_ref:"image-7db888f04ed2827e91ad5145d8fdab0006a8d48b-417x513-jpg",_type:c},_type:b},_type:d,caption:{_type:a,en:"Avraham Elbert, c. 1940s"},credit:{_type:a,en:"The Jews of Tuchin and Kripe in Front of Their Murderers"}}}]}},"3":{content:{settings:{sections:{resistance:{_type:a,en:n},aftermath:{_type:a,en:o},conclusion:{_type:a,en:p},occupation:{_type:a,en:l},intro:{_type:a,en:m}}},subjects:{subject1:{img:{img:{_type:b,asset:{_ref:"image-73c51d4a94b01a07a0568e2435a00127caf3a8be-304x304-png",_type:c}},_type:d,alt:{_type:a,en:"Young girl in a dark dress."}},name:{_type:a,en:"Luba Chomut"}},subject2:{img:{img:{_type:b,asset:{_ref:"image-4fea0ae468b714083c898aff9e3558a7df051d61-304x304-png",_type:c}},_type:d,alt:{en:"Adolescent boy in a shirt and jacket",_type:a}},name:{_type:a,en:"Yosef Zilberberg"}},subject3:{img:{img:{_type:b,asset:{_ref:"image-d38c5b0475ab1692793ebd465d7db5d4da175ae1-304x304-png",_type:c}},_type:d,alt:{_type:a,en:"Young woman in 1940s era clothing"}},name:{_type:a,en:"Mania Schwartzman"}}}}},"4":{labels:{prev:{uk:s,_type:a,en:t},next:{uk:u,_type:a,en:v}},isCollapsed:h},"5":{labels:{prev:{uk:s,_type:a,en:t},next:{uk:u,_type:a,en:v}},isCollapsed:h}},mutations:[]}}("localeString","image","reference","img","span","block","normal",false,"contentImgText","localeRte","rte","Occupation","Introduction","Resistance","Aftermath","Conclusion","Courtesy Yosef Zilberberg","left","наступний розділ","Previous Section","попередній розділ","Next Section")));