__NUXT_JSONP__("/intro/stories/3", (function(a,b,c,d,e,f,g,h,i,j,k,l,m,n,o,p,q,r,s,t){return {data:[{nav:{nav:{next:{_type:a,en:"Explore Map of Tuchyn"},prev:{_type:a,en:"Back to All Stories"},_type:"navLabel"}},metadata:{pageMetadata:{pageTitle:{en:"Meet Mania Schwartzman",_type:a},_type:"pageMetadata"}}}],fetch:{"0":{content:{sections:{resistance:{_type:a,en:k},aftermath:{_type:a,en:l},conclusion:{_type:a,en:m},occupation:{_type:a,en:n},intro:{_type:a,en:o}}},sectionsArr:["intro","occupation","resistance","aftermath","conclusion"],isExpanded:h},"1":{isExpanded:h,languages:{en:{name:"English"},uk:{name:"украї́нська"}}},"2":{content:{sections:[{img:{img:{_type:b,asset:{_ref:"image-296b3e5986ca7b32add09def188edf6a512ab3e0-828x992-png",_type:c}},_type:d,caption:{en:" Tree near house of Schwartzman Family, c. 2012, Courtesy Gideon Kouts.",_type:a}},_type:p,text:{en:{rte:[{markDefs:[],children:[{_type:e,marks:[],text:"Mania Schwartzman was born in Tuchyn in 1924. She had a younger sister Basia and three brothers David, Leib, and Lova. They lived with their parents Getzl and Rivka in a house in the “Zamd” (Sands).",_key:"53ec4c2a14620"}],_type:f,style:g,_key:"b70671c97688"},{style:g,_key:"eaffe337dca6",markDefs:[],children:[{marks:[],text:"The Zamd sat in an area of town with beautiful buildings, a garden of fruit trees, and the Horyn River. Mania’s family had a large tree that stood near the house along the path to the river. It still stands there today.",_key:"f4430157ac990",_type:e}],_type:f}],_type:i},_type:j},_key:"180ce4acff52",imgAlign:"left"},{img:{img:{_type:b,asset:{_type:c,_ref:"image-50ce93466370633c1ad3f714b4554174992af0a6-2240x1488-jpg"}},_type:d,caption:{_type:a,en:"The Schwartzman family house, c. 2012, Courtesy Gideon Kouts."}},_type:"contentImgFull",_key:"6528ed2e6ad7"},{_type:p,text:{_type:j,en:{rte:[{style:g,_key:"6d77367ca906",markDefs:[],children:[{_type:e,marks:[],text:"Mania’s father Getzl ran the town’s transportation company and garage. Getzl was a “soltis,” a leader in the town council and a respected member of the Jewish community. He later became the head of the Judenrat, Jewish Council, during the German occupation.",_key:"2a9f084b41600"}],_type:f}],_type:i}},_key:"3464a1a2a252",imgAlign:"right",img:{img:{_type:b,asset:{_ref:"image-0a32d9663ed0de85d70a9a09cc013260f3e0e5d7-503x629-jpg",_type:c}},_type:d,caption:{_type:a,en:"Getzl Schwartzman, c. 1930s, Tuchin Kripe: In Memory of the Jewish Community."}}},{_key:"07c40fd6d108",_type:"contentText",text:{_type:j,en:{rte:[{children:[{text:"In 1939, when Mania was fifteen, war broke out and the Russians took over Tuchyn. The Russians added classes to the Jewish school drafted young Jews including Mania's brother Leib into the army. In the summer of 1941, the Germans invaded the Soviet Union and drove the Russians out of the area. Many Jews ultimately fought with the Russians against the Nazis during World War II.",_key:"d779a09453580",_type:e,marks:[]}],_type:f,style:g,_key:"016525366896",markDefs:[]},{children:[{_type:e,marks:[],text:"In this exhibition, you will learn what happened under the Germans by hearing more about the Schwartzmans and other Jewish families in Tuchyn.",_key:"6a637231947f0"}],_type:f,style:g,_key:"40ec43e96b8e",markDefs:[]}],_type:i}}}]}},"3":{content:{settings:{sections:{resistance:{_type:a,en:k},aftermath:{_type:a,en:l},conclusion:{_type:a,en:m},occupation:{_type:a,en:n},intro:{_type:a,en:o}}},subjects:{subject1:{img:{img:{_type:b,asset:{_ref:"image-73c51d4a94b01a07a0568e2435a00127caf3a8be-304x304-png",_type:c}},_type:d,alt:{_type:a,en:"Young girl in a dark dress."}},name:{_type:a,en:"Luba Chomut"}},subject2:{img:{img:{_type:b,asset:{_ref:"image-4fea0ae468b714083c898aff9e3558a7df051d61-304x304-png",_type:c}},_type:d,alt:{en:"Adolescent boy in a shirt and jacket",_type:a}},name:{_type:a,en:"Yosef Zilberberg"}},subject3:{img:{img:{_type:b,asset:{_ref:"image-d38c5b0475ab1692793ebd465d7db5d4da175ae1-304x304-png",_type:c}},_type:d,alt:{_type:a,en:"Young woman in 1940s era clothing"}},name:{_type:a,en:"Mania Schwartzman"}}}}},"4":{labels:{prev:{uk:q,_type:a,en:r},next:{uk:s,_type:a,en:t}},isCollapsed:h},"5":{labels:{prev:{uk:q,_type:a,en:r},next:{uk:s,_type:a,en:t}},isCollapsed:h}},mutations:[]}}("localeString","image","reference","img","span","block","normal",false,"rte","localeRte","Resistance","Aftermath","Conclusion","Occupation","Introduction","contentImgText","наступний розділ","Previous Section","попередній розділ","Next Section")));