__NUXT_JSONP__("/intro/stories", (function(a,b,c,d,e,f,g,h,i,j,k){return {data:[{subjectCTA:{_type:a,en:"Read more"},_id:b,title:{_type:a,en:"Tuchyn Before World War II"},subheadText:{_type:"localeText",en:"Click below to meet some of the people in Tuchyn and learn about their life before the war."},_createdAt:"2023-09-29T20:12:35Z",_rev:"PrY6yJV3wLa1jqCBxGuBUJ",pageMetadata:{pageTitle:{_type:a,en:"Meet the People"},_type:"pageMetadata"},_updatedAt:"2023-10-12T18:43:55Z",_type:b,mainText:{_type:"localeRte",en:{_type:"rte",rte:[{style:c,_key:"e53dc06fda6f",markDefs:[],children:[{_type:d,marks:[],text:"In the 1930s, Tuchyn was a shtetl, a predominantly Jewish town, in Poland. About two-thirds of the people in Tuchyn were Jewish. They lived and worked alongside Ukrainians and Poles. Rich farmland surrounded the town and people came from the rural areas to the market there.",_key:"3db41fba2d190"}],_type:e},{markDefs:[],children:[{_type:d,marks:[],text:"In 1933, thousands of miles away in Germany, Adolf Hitler and the Nazi Party came to power. They persecuted Jews and made them second-class citizens. In 1939, Germany launched World War II. When it invaded other countries, it imposed its antisemitic, or anti-Jewish, policies on the countries it occupied. The Germans did not come to Tuchyn initially. Instead, in 1939, the Soviets occupied this area of Poland.",_key:"846f08ffecf90"}],_type:e,style:c,_key:"2ba62522d424"}]}},subhead:{_type:a,en:"Meet the people"}}],fetch:{"0":{content:{sections:{resistance:{_type:a,en:f},aftermath:{_type:a,en:g},conclusion:{_type:a,en:h},occupation:{_type:a,en:i},intro:{_type:a,en:j}}},sectionsArr:["intro","occupation","resistance","aftermath","conclusion"],isExpanded:k},"1":{isExpanded:k,languages:{en:{name:"English"},uk:{name:"украї́нська"}}},"2":{sections:{resistance:{_type:a,en:f},aftermath:{_type:a,en:g},conclusion:{_type:a,en:h},occupation:{_type:a,en:i},intro:{_type:a,en:j}}}},mutations:[]}}("localeString","intro1","normal","span","block","Resistance","Aftermath","Conclusion","Occupation","Introduction",false)));